// ClassifyError
const errorData = $input.first().json;

const workflowName = errorData.workflow?.name || 'Unknown';
const nodeName = errorData.node?.name || 'Unknown';
const errorMessage = errorData.error?.message || 'No message';
const httpCode = errorData.error?.httpCode;
const executionId = errorData.execution?.id;
const timestamp = new Date().toISOString();

const isRetryable = [408, 429, 500, 502, 503, 504].includes(httpCode);
const isClientError = httpCode >= 400 && httpCode < 500;
const isServerError = httpCode >= 500;

let severity = 'low';
if (isServerError || !httpCode) severity = 'high';
else if (isRetryable) severity = 'medium';

const shouldAlert = severity === 'high' || (errorData.execution?.retryOf && severity === 'medium');

const crypto = require('crypto');
const errorHash = crypto
  .createHash('md5')
  .update(workflowName + nodeName + errorMessage)
  .digest('hex')
  .slice(0, 8);

return [{
  json: {
    timestamp,
    workflowName,
    nodeName,
    errorMessage: String(errorMessage).slice(0, 500),
    httpCode: httpCode ?? 'N/A',
    executionId: executionId ?? 'N/A',
    executionUrl: errorData.execution?.url || 'N/A',
    isRetryable,
    severity,
    shouldAlert,
    errorType: isClientError ? 'client_error' : isServerError ? 'server_error' : 'other',
    errorHash
  }
}];
